import React, { useState } from 'react';
import Modal from 'react-modal';
import './styl.css';

Modal.setAppElement('#root');

// Nowe kategorie z nazwami Zwierzęta, Natura, Miasta
const categories = [
  { id: 1, name: 'Zwierzęta', images: ['zwierze1.jpg', 'zwierze2.jpg', 'zwierze3.jpg'] },
  { id: 2, name: 'Natura', images: ['natura1.jpg', 'natura2.jpg', 'natura3.jpg'] },
  { id: 3, name: 'Miasta', images: ['miasto1.jpg', 'miasto2.jpg', 'miasto3.jpg'] },
];

function App() {
  const [selectedCategory, setSelectedCategory] = useState(categories[0]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalImage, setModalImage] = useState('');

  const openModal = (image) => {
    setModalImage(image);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setModalImage('');
  };

  return (
    <div className="App">
      <h1>Galeria zdjęć</h1>

      {/* Kategorie */}
      <div className="categories">
        {categories.map((category) => (
          <button key={category.id} onClick={() => setSelectedCategory(category)}>
            {category.name}
          </button>
        ))}
      </div>

      {/* Galeria obrazów */}
      <div className="gallery">
        {selectedCategory.images.map((image, index) => (
          <div key={index} className="image-container">
            <img
              src={`/images/${image}`}
              alt={image}
              className="thumbnail"
              onClick={() => openModal(image)}
            />
          </div>
        ))}
      </div>

      {/* Modal ze zdjęciem */}
      <Modal isOpen={isModalOpen} onRequestClose={closeModal} contentLabel="Powiększone zdjęcie">
        <button onClick={closeModal} className="close-button">X</button>
        <img src={`/images/${modalImage}`} alt={modalImage} className="modal-image" />
      </Modal>
    </div>
  );
}

export default App;
